var iso=new Isotope(".grid",{itemSelector:".element-item",layoutMode:"fitRows",getSortData:{name:".name",topic:".topic"}}),sortByGroup=document.querySelector(".sort-by-button-group");sortByGroup.addEventListener("click",(function(t){if(matchesSelector(t.target,".button")){var e=t.target.getAttribute("data-sort-value");iso.arrange({sortBy:e})}}));for(var buttonGroups=document.querySelectorAll(".button-group"),i=0;i<buttonGroups.length;i++)buttonGroups[i].addEventListener("click",onButtonGroupClick);function onButtonGroupClick(t){if(matchesSelector(t.target,".button")){var e=t.target;e.parentNode.querySelector(".is-checked").classList.remove("is-checked"),e.classList.add("is-checked")}}